E_D("DROP TABLE IF EXISTS `dw_article`;");
E_C("CREATE TABLE `dw_article` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int(11) DEFAULT '0',
  `name` varchar(255) DEFAULT NULL,
  `littitle` varchar(200) DEFAULT NULL,
  `status` int(2) DEFAULT '0',
  `litpic` varchar(255) DEFAULT NULL,
  `flag` varchar(50) DEFAULT NULL,
  `source` varchar(50) DEFAULT NULL,
  `publish` varchar(50) DEFAULT NULL,
  `is_jump` char(1) DEFAULT NULL,
  `author` varchar(50) DEFAULT NULL,
  `jumpurl` varchar(255) DEFAULT NULL,
  `summary` varchar(255) DEFAULT NULL,
  `province` varchar(20) DEFAULT NULL COMMENT '省份',
  `city` varchar(20) DEFAULT NULL COMMENT '城市',
  `area` varchar(20) DEFAULT NULL COMMENT '区',
  `content` text,
  `order` int(11) DEFAULT '0',
  `hits` int(11) DEFAULT '0',
  `comment` int(11) DEFAULT '0',
  `is_comment` char(1) DEFAULT NULL,
  `user_id` int(11) DEFAULT '0',
  `addtime` varchar(50) DEFAULT NULL,
  `addip` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=112 DEFAULT CHARSET=gbk");
E_D("replace into `dw_article` values('70','10','借入者指南',NULL,'1',NULL,'','','2011-08-22 22:00:03','','','','','','','','<DIV align=left>\r\n<P><FONT face=\"Times New Roman\">1</FONT>．注册账户</P>\r\n<P></P>\r\n<P>&nbsp;填写常用邮箱、用户名和密码，邮箱在以后操作中很重要，用户名取中文和英文都可以，建议您取一个有信任感的用户名，这样能给借出者留下第一好印象，有利于您以后借款进度。密码最好使用数字，字母的组合。（用户名和密码最好写在笔记薄上，以免忘记时方便找回）</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT><FONT face=\"Times New Roman\">2</FONT>．填写个人资料信息</P>\r\n<P></P>\r\n<P>&nbsp;登录后，点击我的账户，填写个人资料，按照网站要求填入真实个人资料信息（如居住地，家属电话，工作单位，房产，学历，社保，财务状况等）。如果个人资料弄虚作假填写，一经发现，将永远禁止在本网站借款。</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT><FONT face=\"Times New Roman\">3</FONT>．实名认证</P>\r\n<P></P>\r\n<P>&nbsp;在安全中心，点击实名认证。填写真实姓名，身份证号等资料，真实姓名填写后不能更改，提现时银行账号必须和实名认证名字一样，否则提现不能成功。</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT><FONT face=\"Times New Roman\">4</FONT>．完善安全中心</P>\r\n<P></P>\r\n<P>&nbsp;点击安全中心，可以修改登录密码、交易密码、设置密码保护问题。</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT><FONT face=\"Times New Roman\">5</FONT>．上传证明资料和申请<FONT face=\"Times New Roman\">VIP</FONT>会员</P>\r\n<P></P>\r\n<P>&nbsp;提供第二代身份证正反面，家属身份证正反面，户口本，个人信用报告<FONT face=\"Times New Roman\">(</FONT>本人到中国人民银行或分行免费打印<FONT face=\"Times New Roman\">)</FONT>，房产证或者房屋租赁合同，社保，近<FONT face=\"Times New Roman\">3</FONT>个月银行流水，近<FONT face=\"Times New Roman\">3</FONT>个月手机通话详单，工作证，毕业证，劳动合同，收入证明，生活照片等资料，以上资料必须是扫描上传或者有效截图上传。</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT>在安全中心，点击<FONT face=\"Times New Roman\">VIP</FONT>信息，选择跟踪客服，<FONT face=\"Times New Roman\">VIP</FONT>会员<FONT face=\"Times New Roman\">180</FONT>元<FONT face=\"Times New Roman\">/</FONT>年（可在借款成功后扣除）。</P>\r\n<P></P>\r\n<P>&nbsp;<FONT face=\"Times New Roman\">6.</FONT>视频认证和手机认证</P>\r\n<P></P>\r\n<P>&nbsp;视频认证前，您需要去网站下载打印借款承诺书（不得私自修改承诺书内容<FONT face=\"Times New Roman\">,</FONT>用于与客服视频认证过程中签字），准备好身份证，户口本原件，找一台有视频的电脑，联系网站客服帮你做视频认证。</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT>手机认证前，请确保您的手机号码必须是实名登记，并且知道手机服务密码（如果您不知道服务密码，可拨打手机服务商人工服务询问）。网站客服会在视频认证过程中打电话给您，并且会登录您的手机网上营业厅，查询您的手机是否实名，以此来确认您的手机是否实名制。</P>\r\n<P></P>\r\n<P>&nbsp;<FONT face=\"Times New Roman\">7. </FONT>发布借款标</P>\r\n<P></P>\r\n<P>&nbsp;通过以上步骤后，点击我要贷款，您就可以发布借款标。您在发布借款标前，请务必阅读网站收费规则，并到网站工具箱计算您的借款成本。借款标满标后若通过网站复审，您借款就成功了。</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT><FONT face=\"Times New Roman\">8. </FONT>提现</P>\r\n<P></P>\r\n<P>&nbsp;在我的账户，点击账号提现，设置提现银行卡号<FONT face=\"Times New Roman\">(</FONT>提现卡号必须和您实名认证时登记的名字一样<FONT face=\"Times New Roman\">) .</FONT>若填写错误，则不能提现。</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT><FONT face=\"Times New Roman\">9. </FONT>充值和还款</P>\r\n<P></P>\r\n<P>&nbsp;在还款时间前几天，请冲值，在我的账户，点击账户充值，选择您的充值方式进行充值。</P>\r\n<P><FONT face=\"Times New Roman\">&nbsp;</FONT>在我的账户，点击正在还款的借款，然后点击还款。（本网站采取等额本息还贷方式）。</P>\r\n<P></P>\r\n<P>&nbsp;<FONT face=\"Times New Roman\">10. </FONT>申请提高借款额度</P>\r\n<P></P>\r\n<P>&nbsp;如果您借款信用积分达到一定程度，可向网站申请提高借款额度。在我的账户，点击额度申请，您就可以申请提高借款额度，网站会客观、公正审核您的额度申请。</P>\r\n<P>&nbsp;</P></DIV>','10','275','0',NULL,'1','1314021710','222.243.13.108');");
E_D("replace into `dw_article` values('71','10','金兰芬信用积分暂行管理办法',NULL,'1',NULL,'','','2011-11-03 20:26:57','','','','','','','','信用积分暂行管理办法 \r\n<P align=center><B>&nbsp;</B></P>\r\n<P>为搭建<FONT face=宋体><FONT face=宋体><FONT face=宋体><FONT face=宋体><FONT face=宋体><FONT face=宋体>金兰芬</FONT></FONT></FONT></FONT>网站</FONT></FONT>用户信用体系，维护用户的权益，现制定信用积分暂行管理办法。</P>\r\n<P><B>一、相关信用证明资料积分的计算</B><B></B></P>\r\n<TABLE cellSpacing=0 cellPadding=0 width=595 border=1>\r\n<TBODY>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>项目名称</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>项目明细</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>信用积分</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>备注说明</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>本人身份证</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>正面</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>2</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>通过实名认证加计15分</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>&nbsp;</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>背面</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>2</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>&nbsp;</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P>家人身份证</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>正面</P>\r\n<P>&nbsp;&nbsp; 背面</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>1</P>\r\n<P>&nbsp;&nbsp; 1</P></TD>\r\n<TD vAlign=top width=288>\r\n<P>需配偶（单身需直系亲属）</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P>实名认证</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>对照上传身份证</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>10分</P></TD>\r\n<TD vAlign=top width=288>\r\n<P>导入身份查询系统，对照上传身份证扫描图片进行认证</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P>邮箱</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>注册邮箱</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>10</P></TD>\r\n<TD vAlign=top width=288>\r\n<P>依据注册时登陆用的邮箱</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>户口本</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>户主页 </P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>&nbsp;</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>与本人页同时上传</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>&nbsp;</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>本人页</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>2</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>&nbsp;</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>&nbsp;</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>家人页</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>1</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>&nbsp;</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>工作证明</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>劳动合同证明</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>2</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>真实在职三个月以上，提供电话地址验证</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>银行流水</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>最近三个月</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>2</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>银行柜台打印盖章，扫描原件上传</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>手机话单</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>最近两个月</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>2</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>本人实名手机通话清单，使用年限</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>住址证明</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=center>水电交费单</P></TD>\r\n<TD vAlign=top width=83>\r\n<P align=center>2</P></TD>\r\n<TD vAlign=top width=288>\r\n<P align=center>本人２分，非本人１分</P></TD></TR>\r\n<TR>\r\n<TD vAlign=top width=118>\r\n<P align=center>&nbsp;</P></TD>\r\n<TD vAlign=top width=106>\r\n<P align=cen